mapper.select:
SELECT
	QUIZ_MSTR_INFO_SEQ
	, SUBJECT_TYPE_CD
	, SRT_NO
	, CONTENT
	, ANSWER
	, (SELECT USER_ANSWER FROM QUIZ_USER_ANS
		WHERE USER_ID = #{userId}
			AND SUBJECT_TYPE_CD = #{subjectTypeCd}
			AND SRT_NO = #{srtNo}
		ORDER BY REG_TS DESC LIMIT 1) AS USER_ANSWER
FROM QUIZ_MSTR_INFO
WHERE DEL_YN = 'N'
	AND SUBJECT_TYPE_CD = #{subjectTypeCd}
	AND SRT_NO = #{srtNo}
