mapper.insert:

	SELECT IFNULL(MAX(QUIZ_USER_ANS_SEQ), 0) + 1 FROM QUIZ_USER_ANS

INSERT INTO QUIZ_USER_ANS (
	QUIZ_USER_ANS_SEQ
	, QUIZ_MSTR_INFO_SEQ
	, USER_ID
	, USER_ANSWER
	, ANSWER_CHK
	, REGIST_ID
	, REGIST_NM
) VALUES (
	#{quizUserAnsSeq}
	, #{quizMstrInfoSeq}
	, #{userId}
	, #{userAnswer}
	, CASE WHEN (SELECT 'O' = (SELECT ANSWER FROM QUIZ_MSTR_INFO WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq})) = '1'
		THEN 'T' ELSE 'F' END
	, 'SYSTEM'
	, 'SYSTEM'
) ON DUPLICATE KEY
UPDATE USER_ANSWER = #{userAnswer}
	, ANSWER_CHK = (SELECT CASE WHEN (SELECT 'O' = (SELECT ANSWER FROM QUIZ_MSTR_INFO WHERE QUIZ_MSTR_INFO_SEQ = #{quizMstrInfoSeq})) = '1'
		THEN 'T' ELSE 'F' END FROM DUAL)
